    public init(options: Options = Options()) {

        if options.mapStyle == .Aerial || options.mapStyle == .AerialWithLabels {
            defaultGamma = 1.3
        } else {
            defaultGamma = 1.0
        }
        
        mapStyle = options.mapStyle
        culture = options.culture
        
        _key = BingMapsAPI.getKey(key: options.key)
        
        _url = options.url
        
        _tileProtocol = options.tileProtocol
        
        _tileDiscardPolicy = options.tileDiscardPolicy
        //        this._proxy = options.proxy;
        credit = Credit(
            text: "Bing Imagery",
            imageUrl: nil/*BingMapsImageryProvider._logoData*/,
            link: "http://www.bing.com"
        )
        
        _tilingScheme = WebMercatorTilingScheme(
            numberOfLevelZeroTilesX : 2,
            numberOfLevelZeroTilesY : 2,
            ellipsoid: options.ellipsoid
        )
        
        
        
        errorEvent = Event()
        
        _ready = false
        
        //var metadataError;
        
        let metadataSuccess = { (data: NSData) -> () in
            
            do {
                let metadataString = String(data: data, encoding: NSUTF8StringEncoding)!
                let metadata = try JSON.decode(string: metadataString, strict: true)
                
                let resource = try metadata.getArray(key: "resourceSets")[0].getArray(key: "resources")[0]
                self._tileWidth = try resource.getInt(key: "imageWidth")
                self._tileHeight = try resource.getInt(key: "imageHeight")
                self._maximumLevel = try resource.getInt(key: "zoomMax") - 1
                self._imageUrlSubdomains = try resource.getArray(key: "imageUrlSubdomains")
                
                 let imageUrlTemplate = try resource
                    .getString(key: "imageUrl")
                    .replace(existingString: "{culture}", self.culture)
                 
                 // Force HTTPS
                 self._imageUrlTemplate = imageUrlTemplate.replace(existingString: "http://", "https://")
                 
                 
                 // Install the default tile discard policy if none has been supplied.
                 //FIXME: Tile discard policy
                 /*if (!defined(that._tileDiscardPolicy)) {
                 that._tileDiscardPolicy = new DiscardMissingTileImagePolicy({
                 missingImageUrl : buildImageUrl(that, 0, 0, that._maximumLevel),
                 pixelsToCheck : [new Cartesian2(0, 0), new Cartesian2(120, 140), new Cartesian2(130, 160), new Cartesian2(200, 50), new Cartesian2(200, 200)],
                 disableCheckIfAllPixelsAreTransparent : true
                 });
                 }*/
                 // FIXME: attribution
                 /*
                 var attributionList = that._attributionList = resource.imageryProviders;
                 if (!attributionList) {
                 attributionList = that._attributionList = [];
                 }
                 
                 for (var attributionIndex = 0, attributionLength = attributionList.length; attributionIndex < attributionLength; ++attributionIndex) {
                 var attribution = attributionList[attributionIndex];
                 
                 attribution.credit = new Credit(attribution.attribution);
                 
                 var coverageAreas = attribution.coverageAreas;
                 
                 for (var areaIndex = 0, areaLength = attribution.coverageAreas.length; areaIndex < areaLength; ++areaIndex) {
                 var area = coverageAreas[areaIndex];
                 var bbox = area.bbox;
                 area.bbox = new Rectangle(
                 CesiumMath.toRadians(bbox[1]),
                 CesiumMath.toRadians(bbox[0]),
                 CesiumMath.toRadians(bbox[3]),
                 CesiumMath.toRadians(bbox[2]));
                 }
                 }*/
                dispatch_async(dispatch_get_main_queue(), {
                    self._ready = true
                })
                 //TileProviderError.handleSuccess(metadataError);*/
            } catch {
                print("Bing metadata decode failed - invalid JSON")
                return
            }
        }
        
        let metadataFailure = { (error: String) -> () in
            print(error)
            /*metadataError = TileProviderError.handleError(metadataError, that, that._errorEvent, message, undefined, undefined, undefined, requestMetadata);*/
        }
        
        
        let metadataUrl = _tileProtocol + _url + "/REST/v1/Imagery/Metadata/" + mapStyle.rawValue
        
        let metadataParameters = [
            "incl" : "ImageryProviders",
            "key" : self._key
        ]
        
        let metadataHeaders = ["Accept": "application/json"]

        let metadataOperation = NetworkOperation(url: metadataUrl, headers: metadataHeaders, parameters: metadataParameters)
        metadataOperation.completionBlock = {
            dispatch_async(dispatch_get_main_queue()) {
                if let error = metadataOperation.error {
                    metadataFailure("An error occurred while accessing \(metadataUrl): \(error)")
                    return
                }
                metadataSuccess(metadataOperation.data)
            }
        }
        metadataOperation.enqueue()
    }
    
    
    /**
    * Gets the credits to be displayed when a given tile is displayed.
    * @function
    *
    * @param {Number} x The tile X coordinate.
    * @param {Number} y The tile Y coordinate.
    * @param {Number} level The tile level;
    * @returns {Credit[]} The credits to be displayed when the tile is displayed.
    *
    * @exception {DeveloperError} <code>getTileCredits</code> must not be called before the imagery provider is ready.
    */
    public func tileCredits (x x: Int, y: Int, level: Int) -> [Credit] {
        return [credit]
    }
    
    /**
    * Requests the image for a given tile.  This function should
    * not be called before {@link ImageryProvider#ready} returns true.
    * @function
    *
    * @param {Number} x The tile X coordinate.
    * @param {Number} y The tile Y coordinate.
    * @param {Number} level The tile level.
    * @param (Function) completion Block
    * @returns {Promise} A promise for the image that will resolve when the image is available, or
    *          undefined if there are too many active requests to the server, and the request
    *          should be retried later.  The resolved image may be either an
    *          Image or a Canvas DOM object.
    *
    * requestImage(x: 0, y: 0, level: 0) {
    * (result: Image?) in
    * if let returnedImage = Image? {
    * // process Image
    *   }
    * }
    * @exception {DeveloperError} <code>requestImage</code> must not be called before the imagery provider is ready.
    */
    public func requestImage(x x: Int, y: Int, level: Int, completionBlock: (CGImage? -> Void)) {
        assert(_ready, "requestImage must not be called before the imagery provider is ready.")
        let url = buildImageUrl(x: x, y: y, level: level)
        loadImage(url: url, completionBlock: completionBlock)
        
    }

    /**
    * Loads an image from a given URL.  If the server referenced by the URL already has
    * too many requests pending, this function will instead return undefined, indicating
    * that the request should be retried later.
    *
    * @param {String} url The URL of the image.
    * @returns {Promise} A promise for the image that will resolve when the image is available, or
    *          undefined if there are too many active requests to the server, and the request
    *          should be retried later.  The resolved image may be either an
    *          Image or a Canvas DOM object.
    */
    public func loadImage (url: String, completionBlock: (CGImage? -> Void)) {
        
        let imageryOperation = NetworkOperation(url: url)
        imageryOperation.completionBlock = {
            if let error = imageryOperation.error {
                print("error: \(error.localizedDescription)")
                return
            }
            #if os(iOS)
                let image = UIImage(data: imageryOperation.data)?.CGImage
            #elseif os(OSX)
                let image = NSImage(data: imageryOperation.data)?.cgImage
            #endif
            dispatch_async(dispatch_get_main_queue(), {
                completionBlock(image)
            })
        }
        imageryOperation.enqueue()
    }
    
